index = {0:"jalen brunson", 1:"mitchell robinson", 2:"mikal bridges", 3:"karl anthony towns", 4:"josh hart", 5:"og anunoby", 6:"jason tatum", 7:"jaylen brown", 8:"al horford", 9:"derrick white", 10:"jrue holiday", 11:"payton pritchard", 12:"kristaps porzingis"}

V = np.transpose(np.asarray([jalen_brunson, mitchell_robinson, mikal_bridges, karl_anthony_towns, josh_hart, og_anunoby, jason_tatum, jaylen_brown, al_horford, derrick_white, jrue_holiday, payton_pritchard, kristaps_porzingis]))

def factorize(V, n):
    model = NMF(n_components=n, init="random", random_state=0)
    W = model.fit_transform(V) # 725x10 , each column is a basis 
    H = model.components_
    return W, H

def generate_component(W, n):
    Wn = W[:, n]
    rW = Wn.reshape((29, 25))
    plt.imshow(rW, cmap="Blues", norm=NoNorm())
    plt.savefig(f"static\\component_{n}.png")

