def get_weight(H, n):
    Hn = H[:, n]
    return Hn

app = Flask(__name__)

@app.route("/")
def main():
    return render_template("home.html")

@app.route("/analysis")
def analysis():
    return render_template("analysis.html")

@app.route("/heatmap_generation", methods=["GET", "POST"])
def heatmap():
    if request.method == "POST":
        rank = int(request.form.get("rank"))
        player = int(request.form.get("player"))
        name = index[player]
        W, H = factorize(V, int(rank))
        for i in range(rank):
            Wn = W[:, i]
            rW = Wn.reshape((29, 25))
            plt.imshow(rW, cmap="Blues", norm=NoNorm())
            plt.savefig(f"static\\generated\\component_{i}.png")
        for i in range(rank):
            Wn = W[:, i]
            Hn = H[:, player][i]
            cV = Hn * Wn
            rV = cV.reshape((29, 25))
            plt.imshow(rV, cmap="Blues", norm=NoNorm())
            plt.savefig(f"static\\generated\\{i}.png")
        return render_template("heatmap.html", display=True, rank=rank, player=player, name=name)
    return render_template("heatmap.html", display=False, rank=0, player=0, name="steve")

